mapper.select:
select l.id, a.name as appName, a.version as appVersion, s.name as serverName, s.ip as serverIp,l.context_id as contextId, l.level,l.message,l.create_time as time from logs l
inner join apps a on l.app_id = a.id
inner join servers s on l.server_id = s.id
where l.app_id = #{appId}


    and l.create_time >= #{begin}




    and l.create_time <= #{end}



    and l.level in

    #{level}



    and l.server_id in

    #{serverId}



    and l.context_id = #{contextId}

order by l.create_time desc
limit #{start},#{count}

select count(0) from logs l
inner join apps a on l.app_id = a.id
inner join servers s on l.server_id = s.id
where l.app_id = #{appId}


    and l.create_time >= #{begin}




    and l.create_time <= #{end}



    and l.level in

    #{level}



    and l.server_id in

    #{serverId}



    and l.context_id = #{contextId}
